import NavBar from './components/NavBar'
import Menu from './components/Menu'
import Cart from './components/Cart'
import FoodsCategory from './components/FoodsCategory'
import './App.scss'

import {useDispatch, useSelector} from "react-redux";
import {fetchFoodList, fetchUserInfo} from "./store/modules/takeaway";
import {useEffect, useState} from "react";
import axios from "axios";

const App = () => {
  // action触发
  const dispatch = useDispatch();
  const [username, setUsername] = useState("");
  const {foodList, activeIndex, userInfo} = useSelector(state => state.foods);
  useEffect(() => {
      // 1.获取用户信息
      async function fetchData() {
          try {
              const response = await axios.get('http://localhost:8081/client/msg?username=zss');
              if (response.data?.username) {
                  setUsername(response.data?.username); // 假设服务器响应包含 username 字段
              } 
          } catch (error) {
              console.error("There was an error fetching the userInfo!", error);
          }
      }
      fetchData();
      // 2.获取食品信息
      dispatch(fetchFoodList());
      // dispatch(fetchUserInfo());
  }, [dispatch]);


  return (
    <div className="home">
      {/* 导航 */}
      <NavBar
          username={username}
      />

      {/* 内容 */}
      <div className="content-wrap">
        <div className="content">
          <Menu />

          <div className="list-content">
            <div className="goods-list">
              {/* 外卖商品列表 */}
              {foodList.map((item, index) => {
                return (
                    activeIndex === index && <FoodsCategory
                    key={item.tag}
                    // 列表标题
                    name={item.name}
                    // 列表商品
                    foods={item.foods}
                  />
                )
              })}
            </div>
          </div>
        </div>
      </div>

      {/* 购物车 */}
      <Cart />
    </div>
  )
}

export default App
